import React from 'react';
import '../App.css';
import { Button } from './Button';
import './HeroSection.css';
import WorkExperience from './pages/WorkExperience';
import {useState} from 'react';

function HeroSection () {

  // add state for input and chat log

  
  const [input,setInput] =useState("");
  const [chatLog,setChatLog] = useState([{
    user: "gpt",
    message:"How can I help you today?"
  },
  {
    user: "me",
    message:"I want to use chat gpt today"
  }

]);
  async function handleSubmit(e){
    e.preventDefault();
    setChatLog([...chatLog,{user:"me",message:'${input'}])
    setInput("");
    const response = await fetch("http://localhost:3080",{
      method:"POST",
      header:{
        "Content-Type":"application/json"
      },
      body:JSON.stringify({
        message:chatLog.map((message) => message.message).join("")
      })
    });
    const data = await response.json();
    console.log(data);
    // console.log('submit')
  }
  return (
    <div className='hero-container'>
      {/* <video src='/videos/video-1.mp4' autoPlay loop muted /> */}
      <img src={process.env.PUBLIC_URL + "/images/bg-1.jpg"}Â />
      {/* <img src='../images/bg-1.jpg'/> */}
      <div className="info">
        <p> I am Nivina Christy.</p>
      </div>
      {/* <div className='hero-btns'>
        <Button
          className='btns'
          buttonStyle='btn--outline'
          buttonSize='btn--large'
        >
          GET STARTED
        </Button>
        <Button
          className='btns'
          buttonStyle='btn--primary'
          buttonSize='btn--large'
          onClick={console.log('hey')}
        >
          WATCH TRAILER <i className='far fa-play-circle' />
        </Button>
      </div> */}
      <div id="aboutme" className="aboutme-container">
        <div className="aboutme-wrap">
        <div className="aboutme-content">
          <h2>About Me</h2>
          <p>
           As a Robotics and Autonomous Engineering master's student at Arizona State University, I hold a bachelor's degree in Electronics and Communication from TKM College of Engineering. With practical experience in the industry, I am an enthusiastic developer driven by a genuine curiosity to broaden my expertise across diverse domains.
          </p>
        </div>
        <div className="aboutme-info">
          <p>Name: Nivina Christy</p>
          <p>Email: nivinachristy10@gmail.com</p>
        </div>
        </div>
        <div className="mypic">
          <img src={process.env.PUBLIC_URL + "/images/mypic.jpg"}Â />
          {/* <img src='/images/mypic.jpg'/> */}
        </div>
      </div>
      <div className='chatbox'>
        <div className='chatlog'> 
         <ChatMessage message={message}/>
          {chatLog.map((message,index)=>(
            <ChatMessage key={index} message={message} />
          ))}
          
        </div>
        <div className="input-holder">
          <form onSubmit={handleSubmit}>
          <input
          rows="1"
          value={input}
          onChange={(e)=> setInput(e.target.value)}
          className="chat-input-textarea" placeholder='Type your message here'>
          </input>
          </form>
        </div>
      </div>
    </div>
  );
}
const ChatMessage =({message}) =>{
  return (
    <div className={'chatMessage ${message.user == "gpt" && "chatgpt"}'}>
      <div className='chatMessageCenter'>
        <div className={'avatar ${message.user =="gpt" &&"chatgpt"}'}>

        </div>
      
      { <div className="message">
        {message.message}
        </div>}
      </div>
      
    </div>
  )
}
export default HeroSection;
